// src/components/DataTable/DataTable.js
import React, { useState, useEffect } from 'react';
import { Table } from 'react-bootstrap';  // Importa la tabla de react-bootstrap

import api from '../../services/api';

const DataTable = () => {
    const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5); // Ajusta el número de elementos por página según tu preferencia

  useEffect(() => {
    fetchData();
  }, [currentPage]);

  const fetchData = async () => {
    try {
      const response = await api.get('/data');
      setData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await api.delete(`/data/${id}`);
      fetchData(); // Actualiza la lista después de eliminar un elemento
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  return (
    <Table striped bordered hover responsive>
      <thead>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Description</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {data.map(item => (
          <tr key={item.id}>
            <td>{item.id}</td>
            <td>{item.name}</td>
            <td>{item.description}</td>
            <td>
          
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default DataTable;
