// src/components/DataTable/DataTable.js
import React, { useState, useEffect } from 'react';
import { Table, Pagination } from 'react-bootstrap';
import api from '../../services/api';

const DataTable = () => {
    const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5); // Ajusta el número de elementos por página según tu preferencia

  useEffect(() => {
    fetchData();
  }, [currentPage]);

  const fetchData = async () => {
    try {
      const response = await api.get('/data');
      setData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  // Calcula el índice del último elemento en la página actual
  const indexOfLastItem = currentPage * itemsPerPage;
  // Calcula el índice del primer elemento en la página actual
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  // Obtiene los elementos de la página actual
  const currentItems = data.slice(indexOfFirstItem, indexOfLastItem);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const handleDelete = async (id) => {
    try {
      await api.delete(`/data/${id}`);
      fetchData(); // Actualiza la lista después de eliminar un elemento
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  return (
    <div>
    <Table striped bordered hover responsive>
      <thead>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Description</th>
        </tr>
      </thead>
      <tbody>
        {currentItems.map((item) => (
          <tr key={item.id}>
            <td>{item.id}</td>
            <td>{item.name}</td>
            <td>{item.description}</td>
          </tr>
        ))}
      </tbody>
    </Table>
    <Pagination>
      {[...Array(Math.ceil(data.length / itemsPerPage)).keys()].map((number) => (
        <Pagination.Item key={number + 1} active={number + 1 === currentPage} onClick={() => paginate(number + 1)}>
          {number + 1}
        </Pagination.Item>
      ))}
    </Pagination>
  </div>
  );
};

export default DataTable;
