// src/components/DataTable/DataTable.js
import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import { Table, Pagination, Button, Modal, Form } from 'react-bootstrap';


const DataTable = () => {
    const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(40); // Ajusta el número de elementos por página según tu preferencia
  const [showEditModal, setShowEditModal] = useState(false);
  const [editItem, setEditItem] = useState({});
  useEffect(() => {
    fetchData();
    updateItemCount();
  }, [currentPage]);

  const updateItemCount = () => {
    setItemCount(data.length);
  };

  const fetchData = async () => {
    try {
      const response = await api.get('/data');
      // Añade la propiedad 'onDelete' a cada objeto de datos
      const dataWithActions = response.data.map(item => ({
        ...item,
        onDelete: () => handleDelete(item.id),
        onEdit: () => handleEdit(item)
      }));
      setData(dataWithActions);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

// Función para manejar la eliminación de un registro
const handleDelete = async (id) => {
    try {
      await api.delete(`/data/${id}`);
      fetchData(); // Actualiza los datos después de la eliminación
      updateItemCount();
    } catch (error) {
      console.error('Error deleting data:', error);
    }
  };

  const handleEdit = (item) => {
    setEditItem(item);
    setShowEditModal(true);
  };

  const [itemCount, setItemCount] = useState(0);

  const handleSaveEdit = async () => {
    try {
      // Actualiza el registro con los nuevos valores
      await api.put(`/data/${editItem.id}`, { name: editItem.name, description: editItem.description });
      fetchData();
      handleCloseEditModal();
    } catch (error) {
      console.error('Error editing data:', error);
    }
  };


  const handleCloseEditModal = () => {
    setShowEditModal(false);
    setEditItem({});
  };

  // Calcula el índice del último elemento en la página actual
  const indexOfLastItem = currentPage * itemsPerPage;
  // Calcula el índice del primer elemento en la página actual
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  // Obtiene los elementos de la página actual
  const currentItems = data.slice(indexOfFirstItem, indexOfLastItem);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
      {/* ... */}
      <Table striped bordered hover responsive>
        <thead>
          <tr>
          
            <th>Nombre</th>
            <th>Apellido</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.length === 0 ? (
            <tr>
              <td colSpan="4">No hay registros</td>
            </tr>
          ) : (
            currentItems.map((item) => (
              <tr key={item.id}>
               
                <td>{item.name}</td>
                <td>{item.description}</td>
                <td>
                  <Button variant="danger" onClick={item.onDelete}>
                    Delete
                  </Button>
                  <Button variant="info" onClick={item.onEdit}>
                    Edit
                  </Button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </Table>
      {/* ... */}
      <Modal show={showEditModal} onHide={handleCloseEditModal}>
        <Modal.Header closeButton>
          <Modal.Title>Editar Registro</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formName">
              <Form.Label>Nombre</Form.Label>
              <Form.Control
                type="text"
                placeholder="Nombre"
                value={editItem.name || ''}
                onChange={(e) => setEditItem({ ...editItem, name: e.target.value })}
              />
            </Form.Group>
            <Form.Group controlId="formDescription">
              <Form.Label>Apellido</Form.Label>
              <Form.Control
                as="textarea"
                placeholder="Apellido"
                value={editItem.description || ''}
                onChange={(e) => setEditItem({ ...editItem, description: e.target.value })}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseEditModal}>
            Cancelar
          </Button>
          <Button variant="primary" onClick={handleSaveEdit}>
            Guardar cambios
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default DataTable;
