// Componente padre que renderiza FormComponent y DataTable
import React, { useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import DataTable from './components/DataTable/DataTable';
import FormComponent from './components/Form/Form';

function App() {
  const [dataUpdated, setDataUpdated] = useState(false);

  const handleDataAdded = () => {
    setDataUpdated(!dataUpdated); // Invierte el estado para forzar una actualización
  };

  return (
    <Container>
      <Row className="mt-5">
        <Col md={8}>
          <DataTable key={dataUpdated} /> {/* Añade una clave única para forzar la actualización */}
        </Col>
        <Col md={4}>
          <FormComponent onDataAdded={handleDataAdded} />
        </Col>
      </Row>
    </Container>
  );
}

export default App;
