// src/components/DataTable/DataTable.js
import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import { Table, Pagination, Button, Modal, Form, Row, Col } from 'react-bootstrap';

const DataTable = () => {
  const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editItem, setEditItem] = useState({});

  useEffect(() => {
    fetchData();
  }, [currentPage, itemsPerPage]);

  const fetchData = async () => {
    try {
      const response = await api.get('/data');
      // Añade la propiedad 'onDelete' a cada objeto de datos
      const dataWithActions = response.data.map(item => ({
        ...item,
        onDelete: () => handleDelete(item.id),
        onEdit: () => handleEdit(item),
      }));
      setData(dataWithActions);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  // Función para manejar la eliminación de un registro
  const handleDelete = async (id) => {
    try {
      await api.delete(`/data/${id}`);
      fetchData(); // Actualiza los datos después de la eliminación
    } catch (error) {
      console.error('Error deleting data:', error);
    }
  };

  const handleEdit = (item) => {
    setEditItem(item);
    setShowEditModal(true);
  };

  const handleSaveEdit = async () => {
    try {
      // Actualiza el registro con los nuevos valores
      await api.put(`/data/${editItem.id}`, { name: editItem.name, description: editItem.description });
      fetchData();
      handleCloseEditModal();
    } catch (error) {
      console.error('Error editing data:', error);
    }
  };

  const handleCloseEditModal = () => {
    setShowEditModal(false);
    setEditItem({});
  };

  // Calcula el índice del último elemento en la página actual
  const indexOfLastItem = currentPage * itemsPerPage;
  // Calcula el índice del primer elemento en la página actual
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  // Obtiene los elementos de la página actual
  const currentItems = data.slice(indexOfFirstItem, indexOfLastItem);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const handleItemsPerPageChange = (e) => {
    setItemsPerPage(Number(e.target.value));
  };

  return (
    <div>
      <Row>
        <Col>
          <h2>Total de Registros: {data.length}</h2>
        </Col>
        <Col>
          <Form>
            <Form.Group controlId="formItemsPerPage">
              <Form.Label>Registros por página:</Form.Label>
              <Form.Control as="select" onChange={handleItemsPerPageChange} value={itemsPerPage}>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value="50">50</option>
              </Form.Control>
            </Form.Group>
          </Form>
        </Col>
      </Row>
      {/* Resto del código permanece igual */}
    </div>
  );
};

export default DataTable;
